-- ----------------------------
-- Table structure for tbl_article
-- ----------------------------
DROP TABLE IF EXISTS `tbl_article`;
CREATE TABLE `tbl_article`  (
  `article_id` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章id，即作者id+时间戳',
  `article_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章标题',
  `article_author_id` int NOT NULL COMMENT '作者id',
  `article_summary` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章摘要',
  `article_is_comment` int NULL DEFAULT 1 COMMENT '是否允许评论',
  `article_rank` int NULL DEFAULT 1 COMMENT '文章排名，用来置顶',
  `article_edit_time` date NULL DEFAULT NULL COMMENT '上一次修改时间',
  `article_post_time` date NULL DEFAULT NULL COMMENT '文章发表时间',
  `article_view_count` int NULL DEFAULT 0 COMMENT '文章阅读量',
  `article_comment_count` int NULL DEFAULT 0 COMMENT '文章评论量',
  `article_like_count` int NULL DEFAULT 0 COMMENT '文章点赞量',
  PRIMARY KEY (`article_id`) USING BTREE,
  INDEX `fk_article_author`(`article_author_id`) USING BTREE,
  CONSTRAINT `fk_article_author` FOREIGN KEY (`article_author_id`) REFERENCES `tbl_user` (`user_id`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = COMPACT;

