DROP TABLE IF EXISTS `tbl_article_content`;
CREATE TABLE `tbl_article_content`  (
  `article_id` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章id',
  `article_html` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文章的html内容',
  `article_md` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文章的md内容',
  PRIMARY KEY (`article_id`) USING BTREE,
  CONSTRAINT `fk_article_edit_id` FOREIGN KEY (`article_id`) REFERENCES `tbl_article` (`article_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = COMPACT;

