-- ----------------------------
-- Table structure for tbl_article_type
-- ----------------------------
DROP TABLE IF EXISTS `tbl_article_type`;
CREATE TABLE `tbl_article_type`  (
  `type_id` int NOT NULL COMMENT '类型id',
  `article_id` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章id',
  PRIMARY KEY (`type_id`, `article_id`) USING BTREE,
  INDEX `fk_article_type_id`(`article_id`) USING BTREE,
  CONSTRAINT `fk_article_type` FOREIGN KEY (`type_id`) REFERENCES `tbl_type` (`type_id`) ON DELETE RESTRICT ON UPDATE CASCADE,
  CONSTRAINT `fk_article_type_id` FOREIGN KEY (`article_id`) REFERENCES `tbl_article` (`article_id`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = COMPACT;

