-- Records of tbl_article_type
-- ----------------------------

-- ----------------------------
-- Table structure for tbl_comment
-- ----------------------------
DROP TABLE IF EXISTS `tbl_comment`;
CREATE TABLE `tbl_comment`  (
  `comment_id` int NOT NULL AUTO_INCREMENT COMMENT '评论的id',
  `pre_comment_id` int NULL DEFAULT NULL COMMENT '上一级评论的id',
  `comment_article_id` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '评论在哪个文章下面',
  `comment_author_id` int NULL DEFAULT NULL COMMENT '评论人的id（匿名没有）',
  `comment_author_nickname` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '评论人昵称',
  `comment_author_email` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '评论人邮箱',
  `comment_content` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `comment_date` date NOT NULL COMMENT '评论日期',
  `comment_author_ip` varchar(15) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '评论人的ip地址',
  `comment_like_count` int NULL DEFAULT 0 COMMENT '评论的点赞数',
  PRIMARY KEY (`comment_id`) USING BTREE,
  INDEX `fk_comment_article_id`(`comment_article_id`) USING BTREE,
  CONSTRAINT `fk_comment_article_id` FOREIGN KEY (`comment_article_id`) REFERENCES `tbl_article` (`article_id`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 10022 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = COMPACT;

