DROP TABLE IF EXISTS `tbl_deleted_article`;
CREATE TABLE `tbl_deleted_article`  (
  `article_id` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `article_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `article_author_id` int NOT NULL,
