-- ----------------------------
-- View structure for v_article_detail
-- ----------------------------
DROP VIEW IF EXISTS `v_article_detail`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `v_article_detail` AS select `ta`.`article_id` AS `article_id`,`tu`.`user_nickname` AS `user_name`,`ta`.`article_title` AS `article_title`,`ta`.`article_edit_time` AS `article_edit_time`,`ta`.`article_post_time` AS `article_post_time`,`ta`.`article_view_count` AS `article_view_count`,`ta`.`article_like_count` AS `article_like_count`,`ta`.`article_comment_count` AS `article_comment_count` from (`tbl_article` `ta` join `tbl_user` `tu` on((`ta`.`article_author_id` = `tu`.`user_id`)));

-- ----------------------------
-- Procedure structure for p_delete_user_deleted_article
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_delete_user_deleted_article`;
delimiter ;;
CREATE PROCEDURE `p_delete_user_deleted_article`(IN id varchar(25))
BEGIN
	DELETE FROM tbl_deleted_article WHERE article_id = id;
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table tbl_article
-- ----------------------------
DROP TRIGGER IF EXISTS `tri_mistake_delete_article`;
delimiter ;;
CREATE TRIGGER `tri_mistake_delete_article` BEFORE DELETE ON `tbl_article` FOR EACH ROW BEGIN
DECLARE m_article_md LONGTEXT;
DECLARE m_article_html LONGTEXT;
set @m_article_md := (SELECT article_md FROM tbl_article_content WHERE article_id = OLD.article_id);
set @m_article_html := (SELECT article_html FROM tbl_article_content WHERE article_id = OLD.article_id);
INSERT INTO tbl_deleted_article VALUES(OLD.article_id, OLD.article_title, OLD.article_author_id, OLD.article_summary, OLD.article_is_comment, OLD.article_rank, OLD.article_edit_time, OLD.article_post_time, @m_article_md, @m_article_html);
END
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
